content:
RESOURCE TYPE: appointment
Display Name: Programare
Description: Programări pentru consultații și tratamente
Resource ID Format: apt_XXXXX sau UUID

DATA SCHEMA:
{
  "date": {
    "type": "string",
    "format": "YYYY-MM-DD",
    "required": true,
    "description": "Data programării"
  },
  "time": {
    "type": "string",
    "format": "HH:mm",
    "required": true,
    "description": "Ora programării"
  },
  "doctor": {
    "type": "object",
    "required": true,
    "properties": {
      "id": {
        "type": "string",
        "required": true,
        "description": "ID-ul medicului (UUID)"
      },
      "name": {
        "type": "string",
        "required": true,
        "description": "Numele complet al medicului"
      }
    }
  },
  "patient": {
    "type": "object",
    "required": true,
    "properties": {
      "id": {
        "type": "string",
        "required": true,
        "description": "ID-ul pacientului (format: pa2508-00007)"
      },
      "name": {
        "type": "string",
        "required": true,
        "description": "Numele complet al pacientului"
      }
    }
  },
  "service": {
    "type": "object",
    "required": true,
    "properties": {
      "id": {
        "type": "string",
        "required": true,
        "description": "ID-ul serviciului/tratamentului (format: tr2508-00005)"
      },
      "name": {
        "type": "string",
        "required": true,
        "description": "Numele tratamentului"
      },
      "duration": {
        "type": "string",
        "required": true,
        "description": "Durata în minute (string)"
      }
    }
  },
  "price": {
    "type": "number",
    "required": false,
    "default": 0,
    "description": "Prețul programării"
  },
  "status": {
    "type": "string",
    "enum": [
      "scheduled",
      "confirmed",
      "completed",
      "cancelled",
      "no_show"
    ],
    "default": "scheduled",
    "description": "Statusul programării"
  },
  "serviceDuration": {
    "type": "string",
    "required": false,
    "description": "Durata serviciului în minute (string, copie din service.duration)"
  },
  "prescription": {
    "type": "string",
    "required": false,
    "default": "",
    "description": "Prescripție medicală"
  },
  "postOperativeNotes": {
    "type": "string",
    "required": false,
    "default": "",
    "description": "Note post-operatorii"
  },
  "images": {
    "type": "array",
    "required": false,
    "default": [],
    "description": "Array cu imagini (URLs sau referințe)"
  },
  "createdAt": {
    "type": "string",
    "format": "ISO8601",
    "auto": true,
    "description": "Data creării (auto-generated)"
  },
  "updatedAt": {
    "type": "string",
    "format": "ISO8601",
    "auto": true,
    "description": "Data ultimei actualizări (auto-generated)"
  }
}

FULL RESOURCE EXAMPLE:
{
  "id": "B0100001-L0100001-apt_12345",
  "businessId": "B0100001",
  "locationId": "L0100001",
  "resourceType": "appointment",
  "resourceId": "apt_12345",
  "data": {
    "date": "2025-10-01",
    "time": "08:40",
    "doctor": {
      "id": "33948842-b061-7036-f02f-79b9c0b4225b",
      "name": "Dr. George Bush"
    },
    "patient": {
      "id": "pa2508-00007",
      "name": "Cristi Geamanu"
    },
    "service": {
      "id": "tr2508-00005",
      "name": "Consultatie Ortodontica",
      "duration": "15"
    },
    "price": 0,
    "status": "scheduled",
    "serviceDuration": "15",
    "prescription": "",
    "postOperativeNotes": "",
    "images": [],
    "createdAt": "2025-10-01T01:42:00.176Z",
    "updatedAt": "2025-10-01T01:42:00.176Z"
  },
  "timestamp": "2025-10-01T01:42:00.176Z",
  "lastUpdated": "2025-10-01T01:42:00.176Z"
}

DATA FIELD EXAMPLE (for creating):
{
  "date": "2025-10-01",
  "time": "08:40",
  "doctor": {
    "id": "33948842-b061-7036-f02f-79b9c0b4225b",
    "name": "Dr. George Bush"
  },
  "patient": {
    "id": "pa2508-00007",
    "name": "Cristi Geamanu"
  },
  "service": {
    "id": "tr2508-00005",
    "name": "Consultatie Ortodontica",
    "duration": "15"
  },
  "price": 0,
  "status": "scheduled",
  "serviceDuration": "15",
  "prescription": "",
  "postOperativeNotes": "",
  "images": []
}

FRONTEND FUNCTION EXAMPLE:
{
  "description": "Când creezi o resursă prin call_frontend_function, trimiți DOAR câmpul 'data'",
  "functionName": "createResource",
  "parameters": {
    "resourceType": "appointment",
    "data": {
      "date": "2025-10-20",
      "time": "14:00",
      "doctor": {
        "id": "33948842-b061-7036-f02f-79b9c0b4225b",
        "name": "Dr. Tucean"
      },
      "patient": {
        "id": "pa2508-00007",
        "name": "Ion Popescu"
      },
      "service": {
        "id": "tr2508-00005",
        "name": "Consultatie",
        "duration": "30"
      },
      "price": 150,
      "status": "scheduled",
      "serviceDuration": "30"
    }
  }
}
